import React, { PureComponent } from 'react';
import ResponsiveContainer from "react-responsive-widget";
import './App.css';
import {
    Label, LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, ReferenceArea
} from 'recharts';

const data = [
    { name: "Noir", cost: 4.11, impression: 100 },
    { name: "Gris", cost: 2.39, impression: 120 },
    { name: "Blanc", cost: 1.37, impression: 150 },
    { name: "Brun", cost: 1.16, impression: 180 },
    { name: "Gold", cost: 2.29, impression: 200 },
  ];
   
export default class Ventes2 extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/ucdl/2yr7e4a5/5/';

  constructor(props) {
    super(props);
  }


  render() {

    const { data, barIndex, left, right, refAreaLeft, refAreaRight, top, bottom, top2, bottom2 } = this.state;
    return (
  
            <LineChart
              width={400}
              height={200}
              data={data}
              onMouseDown = { (e) => this.setState({refAreaLeft:e.activeLabel}) }
              onMouseMove = { (e) => this.state.refAreaLeft && this.setState({refAreaRight:e.activeLabel}) }
            >
              <CartesianGrid strokeDasharray="3 3"/>
              <XAxis 
                allowDataOverflow={true}
                dataKey="name"
                domain={[left, right]}
              />
              <YAxis 
                allowDataOverflow={true}
                domain={[bottom, top]}
                type="number"
                yAxisId="1"
               />
              <YAxis 
                orientation="right"
                allowDataOverflow={true}
                domain={[bottom2, top2]}
                type="number"
                yAxisId="2"
               /> 
              <Tooltip/>
              <Line yAxisId="1" type='natural' dataKey='cost' stroke='#8884d8' animationDuration={300}/>
              <Line yAxisId="2" type='natural' dataKey='impression' stroke='#82ca9d' animationDuration={300}/>
              
              {
                  (refAreaLeft && refAreaRight) ? (
                <ReferenceArea yAxisId="1" x1={refAreaLeft} x2={refAreaRight}  strokeOpacity={0.3} /> ) : null
              
              }
              
            </LineChart> 

      );
    }
  }
