import React, { PureComponent } from 'react';
import ResponsiveContainer from "react-responsive-widget";
import './App.css';
import {
    Label, LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, ReferenceArea
} from 'recharts';

const data = [
    { name: Noir, cost: 4.11, impression: 100 },
    { name: Blanc, cost: 2.39, impression: 120 },
    { name: Gris, cost: 1.37, impression: 150 },
    { name: Brun, cost: 1.16, impression: 180 },
    { name: Gold, cost: 2.29, impression: 200 },
  ];
  
  const getAxisYDomain = (from, to, ref, offset) => {
      const refData = data.slice(from-1, to);
    let [ bottom, top ] = [ refData[0][ref], refData[0][ref] ];
    refData.forEach( d => {
        if ( d[ref] > top ) top = d[ref];
      if ( d[ref] < bottom ) bottom = d[ref];
    });
    
    return [ (bottom|0) - offset, (top|0) + offset ]
  };
  
  const initialState = {
    data,
    left : 'dataMin',
    right : 'dataMax',
    refAreaLeft : '',
    refAreaRight : '',
    top : 'dataMax+1',
    bottom : 'dataMin-1',
    top2 : 'dataMax+20',
    bottom2 : 'dataMin-20',
    animation : true
  };
  


export default class Ventes2 extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/ucdl/2yr7e4a5/5/';

  constructor(props) {
    super(props);
    this.state = initialState;
  }
  
  zoom(){  
  	let { refAreaLeft, refAreaRight, data } = this.state;

		if ( refAreaLeft === refAreaRight || refAreaRight === '' ) {
    	this.setState( () => ({
      	refAreaLeft : '',
        refAreaRight : ''
      }) );
    	return;
    }

		// xAxis domain
	  if ( refAreaLeft > refAreaRight ) 
    		[ refAreaLeft, refAreaRight ] = [ refAreaRight, refAreaLeft ];

		// yAxis domain
    const [ bottom, top ] = getAxisYDomain( refAreaLeft, refAreaRight, 'cost', 1 );
    const [ bottom2, top2 ] = getAxisYDomain( refAreaLeft, refAreaRight, 'impression', 50 );
    
    this.setState( () => ({
      refAreaLeft : '',
      refAreaRight : '',
    	data : data.slice(),
      left : refAreaLeft,
      right : refAreaRight,
      bottom, top, bottom2, top2
    } ) );
  };

	zoomOut() {
  	const { data } = this.state;
  	this.setState( () => ({
      data : data.slice(),
      refAreaLeft : '',
      refAreaRight : '',
      left : 'dataMin',
      right : 'dataMax',
      top : 'dataMax+1',
      bottom : 'dataMin',
      top2 : 'dataMax+50',
      bottom: 'dataMin+50'
    }) );
  }

  render() {

    const { data, barIndex, left, right, refAreaLeft, refAreaRight, top, bottom, top2, bottom2 } = this.state;
    return (
        <div className="highlight-bar-charts">
          <a
            href="javascript: void(0);"
            className="btn update"
            onClick={this.zoomOut.bind( this )}
          >
            Zoom Out
          </a>
  
  
          <p>Highlight / Zoom - able Line Chart</p>
            <LineChart
              width={400}
              height={200}
              data={data}
              onMouseDown = { (e) => this.setState({refAreaLeft:e.activeLabel}) }
              onMouseMove = { (e) => this.state.refAreaLeft && this.setState({refAreaRight:e.activeLabel}) }
              onMouseUp = { this.zoom.bind( this ) }
            >
              <CartesianGrid strokeDasharray="3 3"/>
              <XAxis 
                allowDataOverflow={true}
                dataKey="name"
                domain={[left, right]}
                type="number"
              />
              <YAxis 
                allowDataOverflow={true}
                domain={[bottom, top]}
                type="number"
                yAxisId="1"
               />
              <YAxis 
                orientation="right"
                allowDataOverflow={true}
                domain={[bottom2, top2]}
                type="number"
                yAxisId="2"
               /> 
              <Tooltip/>
              <Line yAxisId="1" type='natural' dataKey='cost' stroke='#8884d8' animationDuration={300}/>
              <Line yAxisId="2" type='natural' dataKey='impression' stroke='#82ca9d' animationDuration={300}/>
              
              {
                  (refAreaLeft && refAreaRight) ? (
                <ReferenceArea yAxisId="1" x1={refAreaLeft} x2={refAreaRight}  strokeOpacity={0.3} /> ) : null
              
              }
              
            </LineChart> 
  
        </div>
      );
    }
  }
