import React, { PureComponent } from 'react';
import {
    PieChart, Pie, Sector, Cell
} from 'recharts';
const data = [{name: 'Group A', value: 370}, {name: 'Group B', value: 290},
                  {name: 'Group C', value: 310}, {name: 'Group D', value: 32}];
const COLORS = ['blue', 'red', 'orange', 'green'];

const RADIAN = Math.PI / 180;                    
const renderCustomizedLabel = ({ cx, cy, midAngle, innerRadius, outerRadius, percent, index }) => {
 	const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
  const x  = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy  + radius * Math.sin(-midAngle * RADIAN);
 
  return (
    <text x={x} y={y} fill="white" textAnchor={x > cx ? 'start' : 'end'} 	dominantBaseline="central">
    	{`${(percent * 100).toFixed(0)}%`}
    </text>
  );
};




export default class Objet2 extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/c9pL8k61/6206/';

  render() {


    return (
    	<PieChart width={800} height={400} onMouseEnter={this.onPieEnter}>
        <Pie
          data={data} 
          cx={200} 
          cy={150} 
          labelLine={false}
          label={renderCustomizedLabel}
          outerRadius={80} 
          fill="#8884d8"
        >
        	{
          	data.map((entry, index) => <Cell fill={COLORS[index % COLORS.length]}/>)
          }
        </Pie>
      </PieChart>
    );
  }
}
